import axios from "axios";
import {useState} from "react";
import {useEffect} from "react";
const SimpleGreet = ()=>{
	const [greeter,setGreeter] = useState("");
	useEffect(()=>{
		axios.get("http://localhost:8080/getter")
		.then((data)=>{console.log(data)});
	},[]);
	const handler = (e)=>{
		e.preventDefault();
		const form = e.target;
		const formData = new FormData(form);
		const formJson = Object.fromEntries(formData.entries());
		axios.post("http://localhost:8080/setter",{
			data:formJson.myData
		})
		.then((response)=>{
			console.log(response);
		})
		.catch((err)=>{
			console.log(err);
		})
	}
	return(
		<div className="simple-greet">
			<form method="post" onSubmit={handler}>
				<label htmlFor="Data">Data : </label>
				<input type="text" name="myData" />
				<button type="submit">enter</button>
			</form>
			<p>
				TODO list : <br /> 
				{greeter}
			</p>
		</div>
	)
}

export default SimpleGreet;
